import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';  // Import useHistory here

const TemplateEditor = () => {
    const { templateName } = useParams();
    const [templateContent, setTemplateContent] = useState('');
    const history = useHistory();  // Use useHistory here

    useEffect(() => {
        // Fetch template content from the server
        fetch(`/api/templates/${templateName}`)
            .then(response => response.json())
            .then(data => setTemplateContent(data.content))
            .catch(error => console.error(error));
    }, [templateName]);

    const handleSave = () => {
        // Save template content to the server
        fetch('/api/saveTemplate', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ templateName, templateContent }),
        })
            .then(() => history.push('/'))
            .catch(error => console.error(error));
    };

    return (
        <div>
            <h2>Edit Template - {templateName}</h2>
            <textarea
                rows="10"
                value={templateContent}
                onChange={e => setTemplateContent(e.target.value)}
            ></textarea>
            <br />
            <button onClick={handleSave}>Save Template</button>
        </div>
    );
};

export default TemplateEditor;
